mapper.select:
SELECT bt_ai_intent.intent_name, bt_ai_intent.intent_detail, bt_ai_intent_phrase.intent_phrase,
bt_ai_paypal_response_templates.template_id, bt_ai_paypal_response_templates.response_time, bt_ai_paypal_response_templates.content_type, bt_ai_paypal_response_templates.content_cn, bt_ai_paypal_response_templates.content_response, bt_ai_paypal_response_templates.content_response_cn, bt_ai_paypal_response_templates.response_params FROM bt_ai_intent , bt_ai_intent_phrase , bt_ai_intent_template_relations , bt_ai_paypal_response_templates WHERE bt_ai_intent_phrase.intent_id = bt_ai_intent.id AND bt_ai_intent_template_relations.intent_id = bt_ai_intent.id AND bt_ai_intent_template_relations.template_id = bt_ai_paypal_response_templates.template_id
AND bt_ai_intent_phrase.intent_phrase=#{text,jdbcType=VARCHAR}

SELECT bt_ai_intent.intent_name, bt_ai_intent.intent_detail, bt_ai_intent_phrase.intent_phrase,
bt_ai_paypal_response_templates.template_id, bt_ai_paypal_response_templates.response_time, bt_ai_paypal_response_templates.content_type, bt_ai_paypal_response_templates.content_cn, bt_ai_paypal_response_templates.content_response, bt_ai_paypal_response_templates.content_response_cn, bt_ai_paypal_response_templates.response_params FROM bt_ai_intent , bt_ai_intent_phrase , bt_ai_intent_template_relations , bt_ai_paypal_response_templates WHERE bt_ai_intent_phrase.intent_id = bt_ai_intent.id AND bt_ai_intent_template_relations.intent_id = bt_ai_intent.id AND bt_ai_intent_template_relations.template_id = bt_ai_paypal_response_templates.template_id AND bt_ai_intent_phrase.intent_phrase=#{text,jdbcType=VARCHAR} AND
bt_ai_paypal_response_templates.order_status = #{order_status,jdbcType=INTEGER} AND bt_ai_paypal_response_templates.order_other_condition = #{other_condition,jdbcType=INTEGER}
